
jQuery(document).ready(function(){

    // languages toggle

    jQuery('.languages li:first-child').on('click', function(){
        jQuery('.languages li').toggleClass('open_lang');

        return false;
    });

    // search toggle

    jQuery('.search i').on('click', function(){
        jQuery(this).prev().fadeToggle(100);
    });

});
